memberdef.initializer:
BOOST_STATIC_VIEWS_NOEXCEPT_IF(noexcept(__VA_ARGS__))            \
        ->decltype(__VA_ARGS__)                                      \
    {                                                                \
        return (__VA_ARGS__);                                        \
    }                                                                \
    /* This is needed to make clang-format think we've just */       \
    /* declared a function. It then doesn't that annoying   */       \
    /* extra indent. */                                              \
    static_assert(true, "")
